import React, {useState} from "react";
import { useMutation, useQuery } from "@apollo/client";
import { ALL_AUTHORS } from "../queries/queries";


export default function AddBook() {
const [name, setName] = useState("")
const [genre, setGenre] = useState("")
const [author, setAuthor] = useState("")
const [addTodo, {mutationData}] = useMutation(ADD_BOOK)
const { loading, error, data } = useQuery(ALL_AUTHORS)
  if (loading) return <p>...Loading data...</p>;
  if (error) return <p>Something went wrong...{JSON.stringify(error)}</p>;



  return (
    <form id="add-book" onSubmit={e => {
      e.preventDefault()
      addTodo(name, genre, author)
    }}>
      <div className="field">
        <label>
          <span>Name</span>
          <input name="name" type="text" value={name} onChange={e => setName(e.target.value)} />
        </label>
      </div>
      <div className="field">
        <label>
          <span>Genre</span>
          <input name="genre" type="text" value={genre} onChange={e => setGenre(e.target.value)} />
        </label>
      </div>
      <div className="field">
        <label >
          <span>Author</span>
          <select value={author} onChange={e => setAuthor(e.target.value )}>
            <option disabled selected value="">
              Select author
            </option>
            {data.allAuthors.map((author) => (
              <option key={author.id} value={author.id}>{author.name}</option>
            ))}
          </select>
        </label>
      </div>
      <button>+</button>
    </form>
  );
}
