import { useMutation, gql, useQuery } from "@apollo/client";

const ALL_BOOKS_QUERY = gql`
  query getBooks {
    allBooks {
      name
      genre
      id
    }
  }
`;

const ALL_AUTHORS = gql`
  query {
    allAuthors {
      name
      age
      id
    }
  }
`;

export { ALL_AUTHORS, ALL_BOOKS_QUERY };
