import React from "react";
import { useMutation, gql } from "@apollo/client";
import { formatError } from "graphql";

const ADD_BOOK = gql`
mutation{
    addBook()
}
`;

export default function AddBook() {
  return (
    <form id="add-book">
      <div className="field">
        <label>
          <span>Name</span>
          <input name="name" type="text" />
        </label>
      </div>
      <div className="field">
        <label>
          <span>Genre</span>
          <input name="genre" type="text" />
        </label>
      </div>
      <div className="field">
        <label>
          <span>Author</span>
          <select>
            <option value={null}>Select author</option>
          </select>
        </label>
      </div>
      <button>+</button>
    </form>
  );
}
