import { gql } from "@apollo/client";

const ALL_BOOKS_QUERY = gql`
  query getBooks {
    allBooks {
      name
      genre
      id
    }
  }
`;

const ALL_AUTHORS = gql`
  query {
    allAuthors {
      name
      age
      id
    }
  }
`;

const ADD_BOOK = gql`
mutation{
    addBook(name: $name , genre: $genre, authorId: $author){
      name
      genre 
      author
    }
}
`

export { ALL_AUTHORS, ALL_BOOKS_QUERY, ADD_BOOK };
