import React, {useState} from "react";
import { useMutation, gql, useQuery } from "@apollo/client";
import { formatError } from "graphql";
import { ALL_AUTHORS } from "../queries/queries";

const ADD_BOOK = gql`
mutation{
    addBook{
      name
    }
}
`

export default function AddBook() {
const [name, setName] = useState("")
const [genre, setGenre] = useState("")
const [author, setAuthor] = useState("")

  const { loading, error, data } = useQuery(ALL_AUTHORS)
  if (loading) return <p>...Loading data...</p>;
  if (error) return <p>Something went wrong...{JSON.stringify(error)}</p>;

  return (
    <form id="add-book" onSubmit={e => {
      e.preventDefault()
      console.log(name, genre, author)
    }}>
      <div className="field">
        <label>
          <span>Name</span>
          <input name="name" type="text" onChange={e => setName(e.key.value)} />
        </label>
      </div>
      <div className="field">
        <label>
          <span>Genre</span>
          <input name="genre" type="text" onChange={e => setGenre(e.key.value)} />
        </label>
      </div>
      <div className="field">
        <label onChange={e => setAuthor(e.target.value )} >
          <span>Author</span>
          <select>
            <option disabled selected value={null}>
              Select author
            </option>
            {data.allAuthors.map((author) => (
              <option value={author.id}>{author.name}</option>
            ))}
          </select>
        </label>
      </div>
      <button>+</button>
    </form>
  );
}
