import React, {useState} from "react";
import { useMutation, gql, useQuery } from "@apollo/client";
import { ALL_AUTHORS } from "../queries/queries";


const ADD_BOOK = gql`
mutation AddBook($name: String! , $genre: String!, $authorId: ID!){
    addBook(name: $name , genre: $genre, authorId: $authorId){
      name
    }
}
`

export default function AddBook() {
const [name, setName] = useState("")
const [genre, setGenre] = useState("")
const [authorId, setAuthorId] = useState("")
const [addBook, mutationResponse] = useMutation(ADD_BOOK)
const { loading, error, data } = useQuery(ALL_AUTHORS)
  if (loading) return <p>...Loading data...</p>;
  if (error) return <p>Something went wrong...{JSON.stringify(error)}</p>;

  console.log(mutationResponse);


  return (
    <form id="add-book" onSubmit={e => {
      e.preventDefault()
      addBook({variables: {name, genre, authorId}, refetchQueries:[{query: ALL_AUTHORS }], awaitRefetchQueries: true})
      // if(window) window.location = window.location
    }}>
      <div className="field">
        <label>
          <span>Name</span>
          <input name="name" type="text" value={name} onChange={e => setName(e.target.value)} />
        </label>
      </div>
      <div className="field">
        <label>
          <span>Genre</span>
          <input name="genre" type="text" value={genre} onChange={e => setGenre(e.target.value)} />
        </label>
      </div>
      <div className="field">
        <label >
          <span>Author</span>
          <select value={authorId} onChange={e => setAuthorId(e.target.value )}>
            <option disabled selected value="">
              Select author
            </option>
            {data.allAuthors.map((author) => (
              <option key={author.id} value={author.id}>{author.name}</option>
            ))}
          </select>
        </label>
      </div>
      <button>+</button>
    </form>
  );
}
